import { useState, createContext } from 'react';

const AuthContext = createContext({
  token: '',
  isLoggedIn: false,
  login: (token) => {},
  logout: (token) => {},
});
let logoutTimer;

const retrieveStroredToken=()=>{
  const storedToken = localStorage.getItem('token');
  const storedExpirationTime = localStorage.getItem('expirationTime');

  const remainingTime = calucateExpirationTime(storedExpirationTime)
  if(remainingTime <= 3600){
    localStorage.removeItem('token');
  localStorage.removeItem('expirationTime');
    return null;
  }
    return {
      token:storedToken,
      duration:remainingTime
    }
}

const calucateExpirationTime = (expirationTime) => {
  const currentTime = new Date().getTime();
  const adjExpirationTime = new Date(expirationTime).getTime();
  const remainingTime = adjExpirationTime - currentTime;
  return remainingTime;
};

export const AuthContextProvider = (props) => {
  let initialToken
  const tokenData = retrieveStroredToken();
  const initalToken = tokenData.token;
  const [token, setToken] = useState(initalToken);
  const userIsLoggedIn = !!token;

  const logOutHandler = () => {
    setToken(null);
    localStorage.removeItem('token');
    if (logoutTimer) {
      clearTimeout(logoutTimer);
    }
  };

  const logInHandler = (token, expirationTime) => {
    setToken(token);
    localStorage.setItem('token', token);
    localStorage.setItem('expirationTime',expirationTime)
    const remainingTime = calucateExpirationTime(expirationTime);

    logoutTimer = setTimeout(logOutHandler, remainingTime);
  };

  const contextValue = {
    token: token,
    isLoggedIn: userIsLoggedIn,
    login: logInHandler,
    logout: logOutHandler,
  };
  return (
    <AuthContext.Provider value={contextValue}>
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
