import classes from './ProfileForm.module.css';
import { useRef ,useContext} from 'react';

const ProfileForm = () => {
  const newPassword = useRef();
  const API_KEY = 'AIzaSyBonyHyJhs54jUzQiHya5lkEKZB8LsxYq4';

  const handleSubmit = (e) => {
    e.preventDefault();
    const enteredPassword = newPassword.current.value;
    fetch(`https://identitytoolkit.googleapis.com/v1/accounts:update?key=${API_KEY}`,{
      method:'POST',
      body:JSON.stringify({
        idToken: '',
        password:enteredPassword,
        returnSecureToken : true,
      }),
      headers:{
        'Content-Type':'application/json'
      }
    })
  };
  return (
    <form className={classes.form}>
      <div className={classes.control}>
        <label htmlFor="new-password">New Password</label>
        <input ref={newPassword} type="password" id="new-password" />
      </div>
      <div className={classes.action}>
        <button onClick={handleSubmit}>Change Password</button>
      </div>
    </form>
  );
};

export default ProfileForm;
